INSERT INTO `citys` (`id`, `name`, `nas`, `region`, `okrug`, `desk`, `chpu`, `rpadej`, `kor1`, `kor2`, `news`, `meteo`) VALUES
(1, 'Москва  ', '11 514 330  ', 'Москва  ', 'Центральный  ', 'Москва – это крупный город России, населенный большим количеством людей.  Москва является туристическим центром России и важным транспортным узлом. Город  представляет собой мировой центр крупного масштаба и важный центр образования. Также здесь много достопримечательностей, которые стоит посмотреть. ', 'Mosqva', 'Москвы', '37.619899', '55.753676', 'Moscow', 'http://meteoinfo.ru/rss/moscow/'),
(2, 'Санкт-Петербург  ', '4 848 742  ', 'Санкт-Петербург  ', 'Северо-Западный  ', 'Санкт-Петербург культурная столица России, раньше именовался Ленинградом, а еще раньше Петроградом. В Питере множество красивых мест и достопримечательностей, которые обязательно должен увидеть жители и гости города. Все, кто собирается его посетить, должны знать архитектурные и культурные достоинства. Центральной достопримечательностью является Эрмитаж – в нём собраны разнообразные музейные экспонаты. ', 'Sanqt-Peterburg', 'Санкт-Петербурга', '30.315868', '59.939095', 'Petersburg', 'http://meteoinfo.ru/rss/forecasts/26063'),
(3, 'Новосибирск  ', '1 498 921  ', 'Новосибирская область  ', 'Сибирский  ', 'Новосибирск находится в юго-восточной части Западно-Сибирской равнины на Приобском плато.  В этом городе континентальная климатическая зона.  Новосибирск – это крупный промышленный центр с развлетвлёнными сетями.  Всего здесь около 214 предприятий. Также в этом  городе присутствует крупный логический комплекс.', 'Novosibirsq', 'Новосибирска', '82.92043', '55.030199', 'Novosibirsk', 'http://meteoinfo.ru/rss/forecasts/29637'),
(4, 'Екатеринбург  ', '1 377 738  ', 'Свердловская область  ', 'Уральский  ', 'Екатеринбург является административным центром  Свердловской области. Это четвертый по населению город после Москвы и Санкт-Петербурга. Также он является главным культурным научно-образовательным центром Уральского региона. Это крупный траспортно-логический узел. ', 'Eqaterinburg', 'Екатеринбурга', '60.605514', '56.838607', 'Yekaterinburg', 'http://meteoinfo.ru/rss/forecasts/28440'),
(5, 'Нижний Новгород  ', '1 250 615  ', 'Нижегородская область  ', 'Приволжский  ', 'Нижний Новгород считается административным центром и крупным городом Приволжского федерального округа. Он делится на две части – верхняя равнина и нижняя на её левом низинном берегу. Раньше город назывался Горький в честь известного писателя. Новосибирск считается транспортным и культурным центром. ', 'Nizhnii-Novgorod', 'Нижнего Новгорода', '44.005986', '56.326887', 'NNovgorod', 'http://meteoinfo.ru/rss/forecasts/27459'),
(6, 'Самара', '1 164 900', 'Самарская область', 'Приволжский', 'Самара – один из крупнейших городов России, который находится на берегу Волги между двумя реками. Кроме этого этот город считается миллионником с большим количеством населения. Самыми главными достопримечательностями Самары считаются православные  и старообрядческие церкви и храмы.', 'Samara', 'Самары', '50.101801', '53.195533', 'Samara', 'http://meteoinfo.ru/rss/forecasts/28900'),
(7, 'Омск', '1 154 000', 'Омская область', 'Сибирский', 'Омск – находится в южной части Западно- Сибирской равны, куда впадает река Омь. Поскольку в Омске присутствуют различные крупные производства, которые служат загрязнителями окружающей среды, экология здесь плохая. Омск – это город, с хорошо развивающейся экономикой среди тридцати других городов.', 'Omsq', 'Омска', '73.368212', '54.989342', 'Omsk', 'http://meteoinfo.ru/rss/forecasts/28698'),
(8, 'Казань', '1 143 546', 'Татарстан', 'Приволжский', 'Казань находится на берегах Волги. У неё богатая удивительная архитектура, одновременно забавная и необычная. Многие туристы мечтают побывать в Храме всех религий, в центре культуры Техниколор, который построил художник Ильдар Ханов. Также большой известностью пользуется Казанский Кремль. ', 'Kazan', 'Казани', '49.106585', '55.795793', 'Kazan', 'http://meteoinfo.ru/rss/forecasts/27595'),
(9, 'Челябинск', '1 130 273', 'Челябинская область', 'Уральский', 'Челябинск – один из крупных российских городов и административных центров. Здесь стоит обратить внимание на транспортный узел (шоссе и железные дороги, аэропорт, магистраль). Кроме этого Челябинск славится металлургическими и машиностроительными предприятиями металлообработки, приборостроения, лёгкой и пищевой промышленности. ', 'Cheliabinsq', 'Челябинска', '61.400856', '55.160283', 'Chelyabinsk', 'http://meteoinfo.ru/rss/forecasts/28645'),
(10, 'Ростов-на-Дону', '1 091 544', 'Ростовская область', 'Южный', 'Ростов – на - Дону находится в юго-восточной части Восточно-Европейской равнины. С левой стороны берега находится множество промышленных предприятий и развлекательных заведений. Сегодня это центр Южного федерального округа. Город характеризуется умеренно континентальным степным климатом. ', 'Rostov-na-Donu', 'Ростова-на-Дону', '39.718705', '47.222531', 'Rostov', 'http://meteoinfo.ru/rss/forecasts/34730'),
(11, 'Уфа', '1 062 300', 'Башкортостан', 'Приволжский', 'Уфа – это крупный экономический культурный спортивный и религиозный центр России, а также он является важным транспортным узлом. Здесь множество интересных достопримечательностей и культурно-исторических памятников. Туристы, которые приезжают сюда, могут прогуляться по паркам и подышать свежим воздухом. ', 'Ufa', 'Уфы', '55.957838', '54.734768', 'Ufa', 'http://meteoinfo.ru/rss/forecasts/28722'),
(12, 'Волгоград', '1 021 244', 'Волгоградская область', 'Южный', 'Волгоград – город, который расположен в южно-восточной части России. Его называют городом-героем. Здесь есть исторические памятники, но их не так много, как  в других городах. Гости города могут побывать в местах, где проходила Сталинградская битва и ознакомиться со всеми достопримечательностями города.', 'Volgograd', 'Волгограда', '44.516939', '48.707103', 'Volgograd', 'http://meteoinfo.ru/rss/forecasts/34560'),
(13, 'Пермь', '1 000 679', 'Пермский край', 'Приволжский', 'Пермь представляет собой город, который располагается в восточной части России. Он является крупным промышленным, научным и культурным центром. В Перми климат умеренно континентальный. Здесь благоприятная окружающая среда и много различных достопримечательностей, которые стоит посмотреть.', 'Perm', 'Перми', '56.234195', '58.010259', 'Perm', 'http://meteoinfo.ru/rss/forecasts/28224'),
(14, 'Красноярск', '1 000 000', 'Красноярский край', 'Сибирский', 'Красноярск  считается крупным промышленным центром России. Здесь много различных достопримечательностей, одной из которых являются Куйсумские горы. В городе присутствуют также старые постройки: терема из дерева, промышленные конструктивные здания, памятники, которые имеют своеобразный стиль. ', 'Krasnoiarsq', 'Красноярска', '92.852545', '56.010569', 'Krasnoyarsk', 'http://meteoinfo.ru/rss/forecasts/29570'),
(15, 'Воронеж', '889 989', 'Воронежская область', 'Центральный', 'В Воронеже представлено множество достопримечательностей. Каждый турист имеет возможность насладиться выходными в этом городе, а также посетить различные достопримечательности. Жители и гости города останутся довольны архитектурными памятниками и очаровательными пейзажами. ', 'Voronezh', 'Воронежа', '39.200287', '51.661535', 'Voronezh', 'http://meteoinfo.ru/rss/forecasts/34123'),
(16, 'Саратов', '836 900', 'Саратовская область', 'Приволжский', 'Саратов находится в южно-восточной части от Москвы с правой стороны реки Волги. Основные достопримечательности, на которые стоит посмотреть, это пешеходный проспект Кирова и парк Победы на Соколовой горе. Также здесь есть множество театров и культурных центров. ', 'Sarahtov', 'Саратова', '46.03582', '51.531528', 'Saratov', 'http://meteoinfo.ru/rss/forecasts/34171'),
(17, 'Краснодар', '744 933', 'Краснодарский край', 'Южный', 'Краснодар раньше назывался Екатринодаром. Сегодня этот город является крупным и современным. Это важный культурный исторический центр России. Здесь очень живописная природа и много архитектурных памятников, которые представляют интерес для туристов.', 'Krasnodar', 'Краснодара', '38.970157', '45.023877', 'Krasnodar', 'http://meteoinfo.ru/rss/forecasts/34927'),
(18, 'Тольятти', '719 484', 'Самарская область', 'Приволжский', 'В городе Тольятти специфическая планировка. Здесь представлены различные районы, которые разделяют крупные лесные массивы. Здесь благоприятная окружающая среда, чистый воздух и прекрасная природа. Туристы могут увидеть памятники и множество различных достопримечательностей.', 'Toliatti', 'Тольятти', '49.41918', '53.508714', 'Samara', 'http://meteoinfo.ru/rss/forecasts/27890'),
(19, 'Ижевск', '628 117', 'Удмуртия', 'Приволжский', 'Ижевск уже давно назван оружейной столицей и Уральским Петербургом. Сначала это был заводской посёлок,  а городом он стал называться в 1918 году. На сегодняшний день этот город является одним из крупных городов России. Здесь отличная архитектура и развлекательная индустрия. Сюда приезжают туристы со всех уголков мира.', 'Izhevsq', 'Ижевска', '53.211463', '56.852775', 'Izhevsk', 'http://meteoinfo.ru/rss/forecasts/28411'),
(20, 'Ульяновск', '613 793', 'Ульяновская область', 'Приволжский', 'Ульяновск считается административным центром Ульяновской области, который находится на Приволжской возвышенности. Здесь собраны различные культурные и архитектурные памятники, которые так любят смотреть гости города. Большой популярностью здесь пользуются храмы и священные места.', 'Ulianowsq', 'Ульяновска', '48.402243', '54.317002', 'Ulyanovsk', 'http://meteoinfo.ru/rss/forecasts/27785'),
(21, 'Барнаул', '612 091', 'Алтайский край', 'Сибирский', 'Барнаул – крупный город Сибири, который быстро и стремительно развивается. Также он является крупнейшим культурным и образовательным сибирским центром. Здесь расположены театры, музеи и разнообразные архитектурные памятники. Особое внимание стоит уделить исторической части города.', 'Barnaul', 'Барнаула', '83.776282', '53.348779', 'Barnaul', 'http://meteoinfo.ru/rss/forecasts/29838'),
(22, 'Владивосток', '592 069', 'Приморский край', 'Дальневосточный', 'Владивосток – считается одним из крупных городов-портов, который находится рядом с Японским морем. Это самый колоритный город России. Он отличается необычным ландшафтом и военно-историческими памятниками. Сюда каждый год приезжают туристы, чтобы ознакомиться с архитектурой и атмосферой Владивостока. ', 'Vladivostoq', 'Владивостока', '131.882421', '43.116391', 'Vladivostok', 'http://meteoinfo.ru/rss/forecasts/31960'),
(23, 'Ярославль', '591 486', 'Ярославская область', 'Центральный', 'В современном Ярославе чисто и уютно. Этот город в основном предназначен для туристов, которые хотят хорошо провести время. Здесь построены различные кафе в европейском стиле, также можно посетить музеи и прогуляться по живописным набережным. ', 'Iaroslavl', 'Ярославля', '39.893822', '57.626569', 'Yaroslavl', 'http://meteoinfo.ru/rss/forecasts/27330'),
(24, 'Иркутск', '587 225', 'Иркутская область', 'Сибирский', 'Иркутск является старинным городом Сибири. Он известен древними зданиями, которые построены в уникальном стиле барокко. Здесь стоит посмотреть на городские каменные застройки и ознакомиться с памятниками деревянной архитектуры. В этом городе присутствует уникальное историческое и культурное наследие. ', 'Irqutsq', 'Иркутска', '104.28066', '52.286387', 'Irkutsk', 'http://meteoinfo.ru/rss/forecasts/30710'),
(25, 'Тюмень', '581 758', 'Тюменская область', 'Уральский', 'Тюмень уже давно считается административным, промышленным и культурным центром России. Этот город впервые основали русские в 1586 году. Самой древней достопримечательностью Тюмени считается Свято-Троицкий монастырь на берегу Тулы. Многие туристы заказывают экскурсии по священным местам. ', 'Tiumen', 'Тюмени', '65.534328', '57.153033', 'Tyumen', 'http://meteoinfo.ru/rss/forecasts/28367'),
(26, 'Махачкала', '577 990', 'Дагестан', 'Северо-Кавказский', 'Махачкала представляет собой город в южной части России. Это очень крупный и развитый город с интересными достопримечательностями и памятниками архитектуры. В последнее время историческая часть города была изменена: произвели ремонтные работы. ', 'Mahachqala', 'Махачкалы', '47.504646', '42.984913', 'Makhachkala', 'http://meteoinfo.ru/rss/forecasts/37472'),
(27, 'Хабаровск', '577 668', 'Хабаровский край', 'Дальневосточный', 'Хабаровск – город, вокруг которого тянутся сухопутные, водные и воздушнее пути. Благодаря этому он стал настоящим транспортным узлом с развивающимися экономическими и торговыми международными связями. Здесь туристы могут наблюдать различные природные зоны и архитектурные достопримечательности.', 'Habarowsq', 'Хабаровска', '135.057732', '48.472584', 'Khabarovsk', 'http://meteoinfo.ru/rss/forecasts/31735'),
(28, 'Оренбург', '570 329', 'Оренбургская область', 'Приволжский', 'Оренбург – это город с удивительной историей. На сегодняшний день он является крупным торговым железнодорожным культурным центром области, в котором царит самобытная атмосфера и расположены различные исторические памятники. Большой известностью здесь пользуется Гостиный двор, который так любят приезжие. ', 'Orenburg', 'Оренбурга', '55.096955', '51.768199', 'Orenburg', 'http://meteoinfo.ru/rss/forecasts/35121'),
(29, 'Новокузнецк', '547 885', 'Кемеровская область', 'Сибирский', 'Новокузнецк – крупный и старый город. Он находится в южной части Западной Сибири. Он считается главным экономическим, транспортным и культурным центром Сибири. Здесь добывают уголь и металлургию. Особое внимание заслуживают музеи и исторические памятники.', 'Novoquznetcq', 'Новокузнецка', '87.136115', '53.757569', 'Kemerovo', 'http://meteoinfo.ru/rss/forecasts/29846'),
(30, 'Кемерово', '532 884', 'Кемеровская область', 'Сибирский', 'Кемерово находится на тридцать первом месте по количеству живущих в нём людей и на сорок девятом месте по площади среди городов России. Он занимает южную сторону Западной Сибири. Кемерово считается важным административным, научным, экономическим и транспортным центром Сибири. ', 'Kemerovo', 'Кемерово', '86.086838', '55.354881', 'Kemerovo', 'http://meteoinfo.ru/rss/forecasts/29645'),
(31, 'Рязань', '525 062', 'Рязанская область', 'Центральный', 'Самый древний город России – это Рязань. Он притягивает туристов своей атмосферой и необычными достопримечательностями. Первым делом приезжие отправляются в Кремль, чтобы ознакомиться с крепостными сооружениями, которые стали защитой центральной части города. Также здесь много церквей и храмов.', 'Riazan', 'Рязани', '39.735243', '54.629148', 'Ryazan', 'http://meteoinfo.ru/rss/forecasts/27730'),
(32, 'Томск', '522 940', 'Томская область', 'Сибирский', 'Томск - город в России и является административным центром. Он находится на востоке Западной Сибири на берегу реки Томь. Также является крупным образовательным и научным инновационным центром. В городе присутствуют деревянные и каменные архитектурные памятники.', 'Tomsq', 'Томска', '84.948691', '56.484725', 'Tomsk', 'http://meteoinfo.ru/rss/forecasts/29430'),
(33, 'Астрахань', '520 662', 'Астраханская область', 'Южный', 'Астрахань – город в России, который объединяют берега Волги. Астрахань имеет основные районы – Ленинский, Трусовский, Советский и Кировский. Город крупный по своим масштабам. Это морской и речной порт Волго-Каспийского бассейна. Поскольку рядом находится железная дорога, через Астрахань отправляется множество грузов. ', 'Astrahan', 'Астрахани', '48.033574', '46.347869', 'Astrakhan', 'http://meteoinfo.ru/rss/forecasts/34880'),
(34, 'Пенза', '519 592', 'Пензенская область', 'Приволжский', 'Пемза – это город, расположенный на холмах. Лучше всего приезжать сюда летом, когда вокруг сочная и зелёная листва и ярко светит солнце. Город богат интересными объектами: живопись, история и литература. Также туристы могут посетить знаменитые музеи и полюбоваться на картины. ', 'Penza', 'Пензы', '45.019529', '53.194546', 'Penza', 'http://meteoinfo.ru/rss/forecasts/27962'),
(35, 'Набережные Челны', '513 242', 'Татарстан', 'Приволжский', 'Набережные Челны – город Татарстана, который является индустриальным гигантом. Раньше он назывался Л.И.Брежневым. География города комплексная. Никому неизвестно, как называются улицы, так как каждая из них имеет свой порядковый номер от 1 до 50. Сегодня Набережные Челны – это центр промышленной агломерации. ', 'Naberezhnye-Chelny', 'Набережных Челнов', '52.39582', '55.743553', 'Kazan', 'http://meteoinfo.ru/rss/forecasts/200018'),
(36, 'Липецк', '508 124', 'Липецкая область', 'Центральный', 'В Липецке вы найдете множество исторических достопримечательностей, старинные парки и аллеи и другие необычные места. Самые главные достопримечательности – это древнейший храм, который представляет собой исторический архитектурный памятник. Здесь множество интересных запоминающихся мест.', 'Leepetcq', 'Липецка', '39.594719', '52.61022', 'Lipetsk', 'http://meteoinfo.ru/rss/forecasts/27930'),
(37, 'Тула', '501 129', 'Тульская область', 'Центральный', 'Тула является большим областным центром. Туристы могут посмотреть здесь Кремль, музеи, в которых хорошо представлена российская культура, музей-усадьбу Толстого «Ясная поляна». Часто приезжие осматривают Благовещенскую церковь и другие необычные храмы.', 'Tula', 'Тулы', '37.617752', '54.193033', 'Tula', 'http://meteoinfo.ru/rss/forecasts/27719'),
(38, 'Киров', '473 668', 'Кировская область', 'Приволжский', 'Киров – это город России, который является административным центром Кировской области. Рядом с ним находится река Вятка. Также Киров является историческим, торговым и промышленным центром. В городе 11 исторических памятников и 22 архитектурных, 3 памятника искусства. Всё это могут посмотреть приезжие. ', 'Kirov', 'Кирова', '49.668077', '58.602901', 'Kirov', 'http://meteoinfo.ru/rss/forecasts/27199'),
(39, 'Чебоксары', '447 929', 'Чувашия', 'Приволжский', 'Важным культурным промышленным центром являются Чебоксары. Это город, который всегда рад приезжающим сюда туристам. Особенностью города является вкусное пиво, о приготовлении которого никто не рассказывает. Здесь есть множество гостиниц и качественных ресторанов с национальной едой. Все очень добрые и гостеприимные. ', 'Cheboqsary', 'Чебоксар', '47.248887', '56.1439', 'Cheboksary', 'http://meteoinfo.ru/rss/forecasts/27581'),
(40, 'Калининград', '431 491', 'Калининградская область', 'Северо-Западный', 'Калининград является удивительным городом. Здесь есть несколько районов, в которых очень тоскливо находиться. Но также множество потрепанных кварталов и достойных внимания. Часто туристы наслаждаются морским музеем и заходят в Рыбную деревню. Здесь много различных необычных достопримечательностей.', 'Kaliningrad', 'Калининграда', '20.507307', '54.70739', 'Kaliningrad', 'http://meteoinfo.ru/rss/forecasts/26702'),
(41, 'Брянск', '415 640', 'Брянская область', 'Центральный', 'Брянск – город, который знаменит воинской славой, в нём сражались мужественные партизаны за свою родину. Здесь можно побродить по густым и величественным лесам, из-за которых город изначально был назван «Дебрянск». Город славится необыкновенно вкусной и полезной едой.', 'Briansq', 'Брянска', '34.364198', '53.2434', 'Bryansk', 'http://meteoinfo.ru/rss/forecasts/26898'),
(42, 'Курск', '414 595', 'Курская область', 'Центральный', 'Возле Курска расположена река Тускарь. В этом городе много истории. Некоторые памятники восстанавливали после Великой Отечественной войны. Всё, что осталось от этого можно увидеть в музеях и жители назвали его город-герой. В Курске очень вкусные шоколадные конфеты, которые изготавливают на местной кондитерской фабрике. ', 'Kursq', 'Курска', '36.192647', '51.730361', 'Kursk', 'http://meteoinfo.ru/rss/forecasts/34009'),
(43, 'Иваново', '409 277', 'Ивановская область', 'Центральный', 'Иваново характеризуется своими архитектурными достопримечательностями. Он славится историко-краеведческим музеем, Музеем ивановского ситца и другими. Здесь также представлены каменные церкви, которые также стоит посетить. Иваново отличается хлопчатобумажным производством.', 'Ivanovo', 'Иваново', '40.973921', '57.000348', 'Ivanovo', 'http://meteoinfo.ru/rss/forecasts/27347'),
(44, 'Магнитогорск', '408 401', 'Челябинская область', 'Уральский', 'Магнитогорск – это славный город, который стоит на реке Урал. Большинство его промышленных предприятий находятся на азиатском берегу. Все, кто приезжает в этот город, никогда не заскучает. Магнитогорск – это мировой центр металлургической промышленности. В нём немного истории – турист может ходить в кинотеатры и клубы. ', 'Magnitogorsq', 'Магнитогорска', '58.984415', '53.411677', 'Chelyabinsk', 'http://meteoinfo.ru/rss/forecasts/28838'),
(45, 'Улан-Удэ', '404 357', 'Бурятия', 'Сибирский', 'Улан-Удэ – город, возле которого находятся реки Селенга и Уда. Это центр, в котором ведётся оптовая торговля чаем. Он является также крупным железнодорожным узлом. Туристы чаще всего гуляют по центру города и восторгаются купеческими дворцами. Именно они являются памятниками древней истории города. ', 'Ulan-Ude', 'Улан-Удэ', '107.584125', '51.833507', 'Ulan-Ude', 'http://meteoinfo.ru/rss/forecasts/30823'),
(46, 'Тверь', '403 726', 'Тверская область', 'Центральный', 'Тверь очень многогранный город. Туристы часто наведываются сюда, чтобы повидать много интересных мест. Здесь заложена многовековая история. Тверь сегодня – современный и красивый город, в котором можно погулять по старинным улочкам и сделать хороший шопинг. ', 'Tver', 'Твери', '35.911896', '56.859611', 'Tver', 'http://meteoinfo.ru/rss/forecasts/27402'),
(47, 'Ставрополь', '398 266', 'Ставропольский край', 'Северо-Кавказский', 'О Ставрополе можно сказать, что он является уютным зеленым городом. Когда-то здесь была большая крепостная стена и много церквей. После того, как была революция, здесь, к сожалению, сохранилось не всё. Очень популярны здесь целебные минеральные воды и грязи, которыми вы можете оздоровиться. ', 'Stavropol', 'Ставрополи', '41.969065', '45.044502', 'Stavropol', 'http://meteoinfo.ru/rss/forecasts/34949'),
(48, 'Симферополь', '362 366', 'Крым', 'Крымский', 'Все, кто находится в Симферополе, имеют возможность насладиться этим замечательным курортом, который также является историческим центром. Одной из главных достопримечательностей является площадь Ленина и различные музеи. Здесь вы полностью окунётесь в историческое прошлое города.', 'Simferopol', 'Симферополя', '34.102411', '44.952116', 'Simferopol', 'http://meteoinfo.ru/rss/forecasts/33946'),
(49, 'Нижний Тагил', '361 883', 'Свердловская область', 'Уральский', 'Нижний Тагил один из главных промышленных уральских центров. Здесь отсутствуют какие-либо значительные водоёмы. Есть только река Тагил с большим количеством притоков. У вас есть возможность побывать на различных выставках и зайти в исторические музеи.', 'Nizhnii-Tagil', 'Нижнего Тагила', '59.98132', '57.910144', 'Yekaterinburg', 'http://meteoinfo.ru/rss/forecasts/28240'),
(50, 'Белгород', '356 426', 'Белгородская область', 'Центральный', 'В Белгороде множество городских скульптур, а также присутствуют старинные храмы. Здесь очень красивые и уютные улицы, на которых тоже есть много интересного. В Белгороде можно наблюдать традиционные  памятники, которые построены в честь знаменитых людей. На то, чтобы посмотреть здесь всё, потребуется не один день.', 'Belgorod', 'Белгорода', '36.588849', '50.597467', 'Belgorod', 'http://meteoinfo.ru/rss/forecasts/34214'),
(51, 'Архангельск', '348 716', 'Архангельская область', 'Северо-Западный', 'Архангельск, в первую очередь, известен тем, что здесь живут поморы, потомственные моряки и выдающиеся учёные. Сегодня здесь передана вся непередаваемая атмосфера торгового города и сохраняет множество историй и традиций. На Архангельском морском вокзале должны побывать все, кто сюда приезжает. ', 'Arhangelsq', 'Архангельска', '40.518735', '64.539304', 'Archangel', 'http://meteoinfo.ru/rss/forecasts/22550'),
(52, 'Владимир', '348 256', 'Владимирская область', 'Центральный', 'Владимир находится в 170 км от Москвы. Это ворота «Золотого Кольца». В самых главных его достопримечательностях проведены ремонтные работы. Владимир ухоженный и чистый город. Информационное обеспечение здесь предоставлено в минимальном количестве. Туристы могут ознакомиться с различными надписями и фресками святых.', 'Vladimir', 'Владимира', '40.40703', '56.129042', 'Vladimir', 'http://meteoinfo.ru/rss/forecasts/27532'),
(53, 'Севастополь', '344 479', 'Севастополь', 'Крымский', 'Севастополь – это город, в который туристы ездят для того чтобы узнать историю и ознакомиться с достопримечательностями. Здесь сохранились красивые особнячки и усадьбы тех времён. В этом городе живет много русских людей, которые встречают гостей с улыбками на лицах. Основные достопримечательности города находятся на двух главных улицах, поэтому ориентироваться здесь легко.', 'Sevastopol', 'Севастополя', '33.52536', '44.616649', 'Simferopol', 'http://meteoinfo.ru/rss/forecasts/200015'),
(54, 'Сочи', '343 285', 'Краснодарский край', 'Южный', 'Сочи – это самый популярный район Краснодарского края. На центральном районе может отдыхать как молодежь, так и семейные пары. Здесь хорошее развитие инфраструктуры, есть различные развлечения и места, где можно отдыхать. Туристы считают, что отдых здесь не самый дешёвый.', 'Sochi', 'Сочи', '39.722882', '43.581509', 'Krasnodar', 'http://meteoinfo.ru/rss/forecasts/37171'),
(55, 'Курган', '333 640', 'Курганская область', 'Уральский', 'Курган считается важным экономическим, научным и культурным центром, а также крупным транспортным узлом. Характеризуется промышленным центром (средним машиностроением, химической, лёгкой и пищевой промышленностью). Здесь есть известный Российский научный центр «Восстановительная травматология и ортопедия». ', 'Kurgan', 'Кургана', '65.344316', '55.441606', 'Kurgan', 'http://meteoinfo.ru/rss/forecasts/28661'),
(56, 'Смоленск', '326 863', 'Смоленская область', 'Центральный', 'Смоленск – это город c красивыми парками, великолепным Успенским кафедральным собором, музеями и смолянами, которые оживили этот город. Главная достопримечательность в городе – зал, где проходят различные концерты и фестивали. Это древний город России и является административным центром.', 'Smolensq', 'Смоленска', '32.044128', '54.782526', 'Smolensk', 'http://meteoinfo.ru/rss/forecasts/26781'),
(57, 'Калуга', '325 185', 'Калужская область', 'Центральный', 'Калуга является городом среднего уровня. Здесь, как и везде имеется развитая промышленность, культура, театры-музеи. Калуга характеризуется архитектурными строениями 18 века. Также присутствуют деревянные памятники и изделия из камня. Стили архитектуры самые разнообразные.', 'Kaluga', 'Калуги', '36.252277', '54.507014', 'Kaluga', 'http://meteoinfo.ru/rss/forecasts/27703'),
(58, 'Чита', '323 964', 'Забайкальский край', 'Сибирский', 'Чита является городом в восточной части России и административным центром. Рядом находится река Чита, которая впадает в реку Инкоду. Самые ведущие отрасли промышленности – энергетика и производство продуктов питания. Самым известным памятником дореволюционного периода является «церковь декабристов», которая была создана в 19 веке. ', 'Chita', 'Читы', '113.499396', '52.033929', 'Chita', 'http://meteoinfo.ru/rss/forecasts/30758'),
(59, 'Орёл', '317 854', 'Орловская область', 'Центральный', 'Город Орёл является административным центром Орловской области. Орел славится крупными транспортными узлами на границах различных районов. Здесь стоит обратить внимание на автомобильные магистрали федерального и республиканского значения. Туристы часто находят здесь интересные архитектурные сооружения.', 'Oryol', 'Орла', '36.063891', '52.971032', 'Orel', 'http://meteoinfo.ru/rss/forecasts/27906'),
(60, 'Волжский', '314 436', 'Волгоградская область', 'Южный', 'Волжский  - это крупный промышленный город Нижнего Поволжья. Здесь представлено активное развитие пассажирских автомобильных перевозок и железнодорожного транспорта. Также здесь хорошее развитие автомобильной сети. Кроме этого имеется большой речной порт.', 'Volzhsqii', 'Волжского', '44.746162', '48.797909', 'Volgograd', 'http://meteoinfo.ru/rss/forecasts/200125'),
(61, 'Череповец', '312 311', 'Вологодская область', 'Северо-Западный', 'Череповец был создан ещё в 1362 году, но городом стал только в 1777. Здесь много различных учебных заведений, городских публичных библиотек, музеев и краеведческих работ. Самым известным архитектурным памятником является Воскресенский собор. Здесь также есть часовня Святителя Николая. ', 'Cherepovetc', 'Череповца', '37.90692', '59.127406', 'Vologda', 'http://meteoinfo.ru/rss/forecasts/27113'),
(62, 'Владикавказ', '311 635', 'Северная Осетия - Алания', 'Северо-Кавказский', 'Владикавказ – это город, который расположен в южной части России,  в Северном Кавказе. Он является промышленным, научным, культурным и образовательным центром. Во Владикавказе есть множество парков и интересных необычных мест, в которых стоит побывать. ', 'Vladiqavqaz', 'Владикавказа', '44.690476', '43.024122', 'Vladikavkaz', 'http://meteoinfo.ru/rss/forecasts/37228'),
(63, 'Мурманск', '307 664', 'Мурманская область', 'Северо-Западный', 'Мурманск славится крупнейшим портом, городом-героем, а также базой ледокольного флота. Этот город отличается уникальной атмосферой, несмотря на то, что  в нём полностью отсутствуют исторические памятники. Все достопримечательности касаются природы и различных военных мемориалов. ', 'Mooremansq', 'Мурманска', '33.07454', '68.969563', 'Murmansk', 'http://meteoinfo.ru/rss/forecasts/22113'),
(64, 'Сургут', '306 703', 'Ханты-Мансийский авт. окр.', 'Уральский', 'Сургут является российским городом и административным центром Сургутского района. Это региональный город, гораздо лучше любого административного центра по населению. Здесь простираются железные и автодороги. В нынешнее время здесь добывают нефть и природный газ. В этом крае множество различной архитектуры, на которую стоит обратить внимание.', 'Surgut', 'Сургута', '73.396204', '61.254052', 'Khanty-Mansiysk', 'http://meteoinfo.ru/rss/forecasts/23849'),
(65, 'Вологда', '301 642', 'Вологодская область', 'Северо-Западный', 'В городе Вологда есть разные достопримечательности, необыкновенная история и самое популярное здесь вологодское масло и лён. Несмотря на то, что появились крупные торговые центры и многоэтажные здания, в городе хранится культурное и историческое наследие. Главная достопримечательность – это дома, украшенные резьбой.', 'Vologda', 'Вологды', '39.891568', '59.220492', 'Vologda', 'http://meteoinfo.ru/rss/forecasts/27037');
